import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { TimerContext } from "./TimerContext";

const Question1 = () => {
    const question = "Which term refers to a template for creating an object?";
    const options = [
        { label: "A", text: "Application" },
        { label: "B", text: "Class" },
        { label: "C", text: "Algorithm" },
        { label: "D", text: "Method" }
    ];
    const correctAnswer = "B";
    const [feedback, setFeedback] = useState("");
    const [stats, setStats] = useState(() => {
        const savedStats = localStorage.getItem("quizStats");
        return savedStats ? JSON.parse(savedStats) : { attempts: 0, correct: 0, incorrect: 0 };
    });

    const checkAnswer = (selected) => {
        let updatedStats = { ...stats, attempts: stats.attempts + 1 };
        if (selected === correctAnswer) {
            setFeedback("✅ Correct! A class in programming serves as a blueprint for creating objects.");
            updatedStats.correct += 1;
        } else {
            setFeedback("❌ Incorrect. The correct answer is B. A class in programming serves as a blueprint for creating objects.");
            updatedStats.incorrect += 1;
        }
        setStats(updatedStats);
        localStorage.setItem("quizStats", JSON.stringify(updatedStats));
    };

    return (
        <div>
            <h2>Data Structures and Algorithms I - C949 Quiz</h2>
            <h4>{question}</h4>
            <div className="options-container">
                {options.map((option) => (
                    <div key={option.label} className="option">
                        <span className="option-text">{option.label}. {option.text}</span>
                    </div>
                ))}
            </div>
            <div className="button-container">
                {options.map((option) => (
                    <button 
                        key={option.label} 
                        className="quiz-button" 
                        onClick={() => checkAnswer(option.label)}
                    >
                        {option.label}
                    </button>
                ))}
            </div>
            <p className="feedback">{feedback}</p>
            <Link to="/question2" className="nav-button">Next Question →</Link>
        </div>
    );
};

export default Question1;
