import React, { useState, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { TimerContext } from "./TimerContext";

const Question40 = () => {
    const navigate = useNavigate();
    const timerContext = useContext(TimerContext);

    if (!timerContext) {
        return <p>Loading...</p>; // Prevents crash if context is undefined
    }

    const { timeLeft, resetTimer } = timerContext;

    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${minutes}:${secs < 10 ? "0" : ""}${secs}`;
    };

    const question1 = "40. What will the peek() operation from this stack return? 8,9,3,5 (top is 8)";
    const options1 = [
        { label: "A", text: "1" },
        { label: "B", text: "3" },
        { label: "C", text: "8" }  // ✅ Correct answer
    ];

    const correctAnswer1 = "C";  // ✅ "8" is the correct answer

    const question2 = "What is the set that results from set1 intersection set2, given these sets? set1 = (69,82,47) set2 = (11,82)";
    const options2 = [
        { label: "A", text: "(82)" },  // ✅ Correct answer
        { label: "B", text: "(69,82,47,11)" },
        { label: "C", text: "(69,82,47]" },
        { label: "D", text: "(11,82]" }
    ];

    const correctAnswer2 = "A";  // ✅ "(82)" is the correct answer

    const [feedback, setFeedback] = useState("");

    const checkAnswer = (selected, correct) => {
        if (selected === correct) {
            setFeedback("✅ Correct! 82 is the only number in both sets.");
        } else {
            setFeedback(`❌ Incorrect. The correct answer is **A**. 82 is the only number in both sets.`);
        }
    };

    // ✅ Handle Reset Button Click
    const handleReset = () => {
        resetTimer();
        navigate("/");
    };

    return (
        <div>
            <h2>Data Structures and Algorithms I - C949 Quiz</h2>
            <div className="timer-container">
                <p className="timer">⏳ Time Remaining: {formatTime(timeLeft)}</p>
            </div>

            <h4>{question1}</h4>
            <div className="options-container">
                {options1.map((option) => (
                    <div key={option.label} className="option">
                        <span className="option-text">{option.label}. {option.text}</span>
                    </div>
                ))}
            </div>
            <div className="button-container">
                {options1.map((option) => (
                    <button 
                        key={option.label} 
                        className="quiz-button" 
                        onClick={() => checkAnswer(option.label, correctAnswer1)}
                    >
                        {option.label}
                    </button>
                ))}
            </div>

            <h4>{question2}</h4>
            <div className="options-container">
                {options2.map((option) => (
                    <div key={option.label} className="option">
                        <span className="option-text">{option.label}. {option.text}</span>
                    </div>
                ))}
            </div>
            <div className="button-container">
                {options2.map((option) => (
                    <button 
                        key={option.label} 
                        className="quiz-button" 
                        onClick={() => checkAnswer(option.label, correctAnswer2)}
                    >
                        {option.label}
                    </button>
                ))}
            </div>

            <p className="feedback">{feedback}</p>

            <div className="nav-buttons">
                <Link to="/question39" className="nav-button">← Back to Question 39</Link>
                <Link to="/question41" className="nav-button">Next Question →</Link>
                <button onClick={handleReset} className="reset-button">❌ Quit & Restart</button>
            </div>
        </div>
    );
};

export default Question40;
